import React, { useState } from "react";
function Formfilled() {
    let user = {};
    let editUser = {};

    const [arr, setArr] = useState([])
    const [editIndex, setEditIndex] = useState(false)
    const [issubmit, setIssubmit] = useState(false)
    const [selectedOption, setSelectedOption] = useState(false)

    const handleSubmit = (event) => {
        event.preventDefault()
        console.log(user);
        setArr([...arr, user])
        user = { name: "", currentWeight: "", height: "" }
        setIssubmit(true)
        if (user.currentWeight > 100) {
            alert(" current weight Not Greater than 100")
        }
        if (user.name === '') {
            alert("member name is not filled")

        }
        if (user.height > 600) {
            alert("Height enterd in centemeter")

        }
    };
    console.log(arr);

    function handleChange(event) {
        const { name, value } = event.target
        console.log(name)
        user[name] = value
    }

    function handleWeightChange(e) {
        const { name } = e.target
        if (e.target.value.length > e.target.maxLength) {
            e.target.value = e.target.value.slice(0, e.target.maxLength)
            user[name] = e.target.value
        }

    }
    function handleEdit(e) {
        e.preventDefault()
        setArr([...arr, editUser]);
        editUser = { editName: "", editCurrentWeight: "", editHeight: "" };
        setEditIndex(false);
    }


    const handleOption = (index) => {
        const updatedForm = [...arr]
        updatedForm[index].selectedOption = !updatedForm[index].selectedOption;
        setArr(updatedForm)
        setSelectedOption(!selectedOption);
    };


    const handleEditProcess = (index) => {
        if (index !== false) {
            setEditIndex(index);
            const editedItem = arr[index];
            setArr(editedItem.editUser);
            setSelectedOption(!selectedOption);

        }
    }

    const handleDelete = (index) => {
        const updatedForm = [...arr];
        updatedForm.splice(index, 1);
        setArr(updatedForm);

    };

    return (

        <div className="container-fluid m-3 bg-lite ">
            <div className="column">
                <div className="mt-5">
                    <h2>GYM Membership Form</h2>
                    <div className='row mt-5 '>
                        <div className='column m-3'>
                            <label className="d-flex  align-items-center">Name:</label>
                            <input type='text' name="name" value={user.name} maxLength={15} onChange={handleChange}></input>
                        </div>

                        <div className='column m-3'>
                            <label className="d-flex  align-items-center ">CurrentWeight (kg)*:</label>
                            <input type="number" name="currentWeight" value={user.currentWeight} maxLength={2} onChange={handleWeightChange}></input>
                        </div>

                        <div className='column m-3'>
                            <label className="d-flex  align-items-center ">Height in(cm)*:</label>
                            <input type="number" name="height" value={user.height} maxLength={3} onChange={handleWeightChange}></input>
                        </div>

                    </div>
                    <div className='w-30 h-30 d-flex' >
                        <button className='btn btn-primary' onClick={editIndex !== false ? handleEdit : handleSubmit} type='submit' >{editIndex !== false ? 'UPDATE' : 'SUBMIT'}</button>

                    </div>
                </div>
                <div className="col-12 mt-5" >
                    <table className='table'>

                        <tr className='table-active'>
                            <th >Name</th>
                            <th >CurrentWeight</th>
                            <th> Height </th>

                        </tr>

                        {issubmit && arr.map((item, index) => {

                            return (
                                <tbody >
                                    <tr >
                                        <td >{item.editName} </td>
                                        <td >{item.editCurrentWeight}</td>
                                        <td >{item.editHeight}</td>
                                        <tr>
                                            <div onClick={() => { handleOption(index); }}>
                                                <img alt='three dots' src='https://w7.pngwing.com/pngs/948/504/png-transparent-computer-icons-material-design-hamburger-button-menu-menu-text-rectangle-computer-wallpaper-thumbnail.png' height='20' width='20'></img></div>
                                            {item.selectedOption &&
                                                <div className='menu-options'>
                                                    <div className='option' onClick={() => handleEditProcess(index)}>Edit</div>
                                                    <div className='option' onClick={() => handleDelete(index)}>Delete</div>
                                                </div>
                                            }

                                        </tr>
                                    </tr>
                                </tbody>
                            )
                        })}


                    </table>
                </div>
            </div>
        </div >
    );
}
export default Formfilled;
