import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
function Form() {
    const [formData, setFormData] = useState({ Name: "", Age: "", Gender: "", Email: "" });
    const [errors, setErrors] = useState({});
    const [submitted, setSubmitted] = useState(false)
    const navigate = useNavigate()

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        setFormData({
            ...formData,
            [name]: value
        });
    };

    const validateForm = () => {
        let isValid = true;
        const newErrors = {};
        if (!formData.Name) {
            newErrors.Name = "Name is required";
            isValid = false;
        }
        if (!formData.Age) {
            newErrors.Age = "Age is required";
            isValid = false;
        }
        if (!formData.Gender) {
            newErrors.Gender = "Gender is required";
            isValid = false;
        }
        if (!formData.Email) {
            newErrors.Email = "Email is required";
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };
    const handleSubmit = (event) => {
        event.preventDefault()

        if (validateForm()) {
            console.log("Form data:", formData);
            alert("Form is valid Successfully")
            setSubmitted(true);
        } else {
            alert("Form is not valid")
        }
        axios.post('http://localhost:1234/registerUser', {
            name: "rfjkn",
            age: "11",
            email: "nrfnrfjknf",
            gender: "male"

        })
            .then((response) => {
                navigate('/listout')
                console.log(response);

            })
            .catch((error) => {
                console.log(error);
            })


    };



    function handleAgeChange(e) {
        const { name } = e.target
        if (e.target.value.length > e.target.maxLength) {
            e.target.value = e.target.value.slice(0, e.target.maxLength)  //type of number ah max length  apply panrathu
            user[name] = e.target.value
        }

    }
    return (

        <div className="container-fluid m-3 bg-lite ">
            <div className="column">
                <div className="mt-5">
                    <h2>GYM Membership Form</h2>
                    <div className='row mt-5 '>
                        <div className='column m-3'>
                            <label className="d-flex  align-items-center">Name:</label>
                            <input type='text' name="name" value={user.name} maxLength={15} onChange={handleChange}></input>
                        </div>

                        <div className='column m-3'>
                            <label className="d-flex  align-items-center ">Age:</label>
                            <input type="number" name="Age" value={user.Age} maxLength={2} onChange={handleAgeChange}></input>
                        </div>

                        <div className='column m-3'>
                            <label className="d-flex  align-items-center ">Gender:</label>
                            <select name="Gender ">
                                <option value="Male">Male</option>
                                <option value="Female">Female</option>
                            </select>
                        </div>
                        <div className='column m-3'>
                            <label className="d-flex  align-items-center ">Email:</label>
                            <input type="text" name="Email" value={user.Email} onChange={handleChange}></input>
                        </div>

                    </div>
                    <div className='w-30 h-30 d-flex' >
                        <button className='btn btn-primary' type="onsubmit" onClick={handleSubmit}> SUBMIT</button>
                    </div>
                </div>
            </div>
        </div >
    );
}
export default Form;
