import React from "react";
import { useState, useEffect } from "react";
import ProgressBar from "./Progressbar";
function App() {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const timer = setInterval(() =>
      setProgress(Math.floor(Math.random() * 100) + 1), 2000);
    // console.log('fff');;
    return () => {
      clearInterval(timer);
      // console.log('wwerr')
    };
  }, [progress]);

  return (
    <div>
      <ProgressBar />
    </div>

  );
}
export default App;
