import React from "react";
import { useState } from 'react'
const Weightage = () => {
  const [name, setName] = useState('')
  const [description, setDescription] = useState('')
  const [weightCount, setWeightCount] = useState('');
  let [issubmit, setIssubmit] = useState(false)
  let [totalWeightageCount, setTotalWeightageCount] = useState(0)
  const [form, setForm] = useState([]);
  const [selectedOption, setSelectedOption] = useState(false);
  const [editIndex, setEditIndex] = useState(false);
  let weightArray = []

  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [editWeightCount, setEditWeightCount] = useState('');


  const handleSubmit = () => {
    setIssubmit(true)
    if (weightCount > 100) {
      alert("Not Greater than 100")
    }
    else if (weightCount < 100) {
      if ((name !== '') && (description !== '') && (weightCount !== '')) {
        weightArray.push(parseInt(weightCount))
        console.log(weightArray)
        for (let i = 0; i < weightArray.length; i++) {
          totalWeightageCount = (parseInt(totalWeightageCount) + parseInt(weightCount));
        }
        const user = { name, description, weightCount, totalWeightageCount };
        setForm([...form, user]);
        setName('');
        setDescription('');
        setWeightCount('');

        setTotalWeightageCount(totalWeightageCount);
      }
    }
  };

  const handleOption = (index) => {
    const updatedForm = [...form]
    updatedForm[index].selectedOption = !updatedForm[index].selectedOption;
    setForm(updatedForm)
    setSelectedOption(!selectedOption);
  };

  const handleDelete = (index) => {
    const updatedForm = [...form];
    updatedForm.splice(index, 1);
    setForm(updatedForm);

  };

  const handleEdit = () => {
    if (editWeightCount > 100) {
      alert('Weightage count not greater than 100');
    } else if (editWeightCount < 100) {
      if ((editName !== "") && (editDescription !== "") && (editWeightCount !== "")) {
        const updatedForm = [...form];
        updatedForm[editIndex] = {
          name: editName,
          description: editDescription,
          weightCount: editWeightCount,

        };
        setForm(updatedForm);
        setEditIndex(false);
        setEditName('');
        setEditDescription('');
        setEditWeightCount('');
      }
    }
  };


  const handleEditProcess = (index) => {
    if (index !== false) {
      setEditIndex(index);
      const editedItem = form[index];
      setEditName(editedItem.name);
      setEditDescription(editedItem.description);
      setEditWeightCount(editedItem.weightCount);
      setSelectedOption(!selectedOption);

    }
  }

  return (
    <>


      <div class="container-fluid m-3 bg-lite ">
        <div class='card m-5   p-3'>
          <div class="column">
            <div class="mt-5">
              <h4>WEIGHTAGE COUNT FORM</h4>
              <div class='row mt-5 '>
                <div class='column m-3'>
                  <label className="d-flex justify-content-flex-start align-items-center">Name:</label>
                  <input type='text' value={editIndex !== false ? editName : name} onChange={(e) => (editIndex !== false ? setEditName(e.target.value) : setName(e.target.value))}></input>
                </div>
                <div class='column m-3'>
                  <label className="d-flex justify-content-flex-start align-items-center ">Description:</label>
                  <textarea style={{ resize: 'none', height: 30 }} type='text' value={editIndex !== false ? editDescription : description} onChange={(e) => (editIndex !== false ? setEditDescription(e.target.value) : setDescription(e.target.value))}></textarea>
                </div>
                <div class='column m-3'>
                  <label className="d-flex justify-content-flex-start align-items-center ">WeightageCount:</label>
                  <input value={editIndex !== false ? editWeightCount : weightCount} type='text' inputMode='numeric' onChange={(e) => (editIndex !== false ? setEditWeightCount(e.target.value) : setWeightCount(e.target.value))}></input>
                </div>
              </div>
              <div className='w-30 h-30 d-flex' >

                <button class='btn btn-primary' onClick={editIndex !== false ? handleEdit : handleSubmit} type='submit' >{editIndex !== false ? 'UPDATE' : 'SUBMIT'}</button>

              </div>




            </div>
            <div class="col-12 mt-5" >
              <table class='table'>

                <tr class='table-active'>
                  <th >Name</th>
                  <th >Description</th>
                  <th >WeightCount</th>
                  <th >TotalWeightAgeCount</th>
                  <th></th>
                </tr>


                {issubmit &&
                  form.map((item, index) => {

                    return (
                      <tbody >
                        <tr >
                          <td >{item.name} </td>
                          <td >{item.description}</td>
                          <td >{item.weightCount}</td>
                          <td >{item.totalWeightageCount}</td>
                          <tr>
                            <div onClick={() => { handleOption(index); }}>
                              <img alt='three dots' src='https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTW3tspw6zBcKgOwHnSUiMknqhJuDf-4fiafe-JnG0&s' height='20' width='20'></img></div>
                            {item.selectedOption &&
                              <div class='menu-options'>
                                <div class='option' onClick={() => handleEditProcess(index)}>Edit</div>
                                <div class='option' onClick={() => handleDelete(index)}>Delete</div>
                              </div>
                            }

                          </tr>
                        </tr>
                      </tbody>
                    )
                  })}


              </table>
            </div>
          </div>
        </div>
      </div>
    </>

  )
}

export default Weightage
