import React, { useState, useEffect, } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
function Listout() {
    const [users, setUsers] = useState([])
    const [edituser, setEditUser] = useState([])
    const navigate = useNavigate()
    useEffect(() => {
        GetApi()

    }, []);

    const GetApi = () => {
        axios.get('http://localhost:1234/getUser')
            .then((res) => {
                console.log(res);
                setUsers(res.data.data)
            })
    }

    const handleDelete = (userid) => {
        axios.delete('http://localhost:1234/deleteUser', { params: { id: userid } })
            .then((res) => {
                const del = users.filter(user => userid !== user._id)
                setUsers(del)

            })
    };
    const handleEditProcess = (index) => {
        if (index !== false) {
            const editedItem = users[index];
            setEditUser(editedItem)

        }
        updateUser()
    }

    const updateUser = () => {
        axios
            .put('http://localhost:1234/updateUser')
            .then((responce) => {
                navigate('/edituser')
                console.log(responce)
            })
            .catch((err) => {
                console.log("err")
            })
    }
    console.log(users);
    return (
        <div Form className="container-fluid m-3 bg-lite ">
            <div className="column">
                <div className="col-12 mt-5" >
                    <table className='table'>

                        <tr className='table-active'>
                            <th > Id     </th>
                            <th > Name   </th>
                            <th > Age    </th>
                            <th > Gender </th>
                            <th > Email  </th>
                        </tr>

                        {users.length > 0 && users.map(({ _id, name, age, gender, email }) => {
                            console.log(users);
                            return (
                                <tbody >
                                    <tr key={_id}>
                                        <td >  {_id}   </td>
                                        <td >  {name}   </td>
                                        <td >  {age}    </td>
                                        <td >  {gender} </td>
                                        <td >   {email}  </td>
                                        <tr>
                                            <td>
                                                <button className="btn btn-primary" onClick={handleEditProcess} >  Edit </button>
                                                <button className="btn btn-danger" onClick={() => { handleDelete(_id); }}>Delete</button>

                                            </td>
                                        </tr>

                                    </tr>
                                </tbody>
                            )
                        })}


                    </table>
                </div>
            </div>
        </div>
    )

}
export default Listout;
