import React, { useState, useEffect } from "react";
import axios from "axios";
function Listout() {
    const [form, setForm] = useState([])
    const [user, setUser] = useState([])
    const [selectedOption, setSelectedOption] = useState(false)
    const [editName, setEditName] = useState("")
    const [editAge, setEditAge] = useState("")
    const [editEmail, setEditEmail] = useState("")
    const [editGender, setEditGender] = useState("")

    useEffect(() => {
        console.log(user)
        axios.get('http://localhost:1234/getUser')
            .then((res) => {
                console.log(res);
                setUser(res.data.data)
            })
        return
    }, []);

    const handleOption = (index) => {
        const updatedForm = [...form]
        updatedForm[index].selectedOption = !updatedForm[index].selectedOption;
        setForm(updatedForm)
        setSelectedOption(!selectedOption);
    };

    const handleDelete = (index) => {
        const updatedForm = [...form,];
        updatedForm.splice(index, 1);
        setForm(updatedForm);
    };
    const handleEditProcess = (index) => {
        if (index !== false) {

            const editedItem = form[index];
            setEditName(editedItem.editName);
            setEditAge(editedItem.editAge);
            setEditGender(editedItem.editGender);
            setEditEmail(editedItem.editEmail)
            setSelectedOption(!selectedOption);

        }
    }
    console.log(user);
    return (
        <div Form className="container-fluid m-3 bg-lite ">
            <div className="column">
                <div className="col-12 mt-5" >
                    <table className='table'>

                        <tr className='table-active'>
                            <th >Name</th>
                            <th >Age</th>
                            <th> Gender </th>
                            <th> Email </th>

                        </tr>

                        {user.length > 0 && user.map((item) => {
                            console.log(user);
                            return (
                                <tbody >
                                    <tr >
                                        <td >{item.name} </td>
                                        <td >{item.age}</td>
                                        <td >{item.gender}</td>
                                        <td >{item.email}</td>
                                        <tr>
                                            <div onClick={() => { handleDelete(); }}>
                                                <img alt='three dots' src='https://w7.pngwing.com/pngs/948/504/png-transparent-computer-icons-material-design-hamburger-button-menu-menu-text-rectangle-computer-wallpaper-thumbnail.png' height='20' width='20'></img></div>
                                            {item.selectedOption &&
                                                <div className='menu-options'>
                                                    <div className='option' onClick={() => handleEditProcess()}>Edit</div>
                                                    <div className='option' onClick={() => handleDelete()}>Delete</div>
                                                </div>
                                            }

                                        </tr>
                                    </tr>
                                </tbody>
                            )
                        })}


                    </table>
                </div>
            </div>
        </div>
    )
}
export default Listout;
