      <div className="column">
        <div className="mt-5">
          <h2>GYM Membership Form</h2>
          <div className='row mt-5 '>
            <form onSubmit={handleSubmit}>

              <div className='column m-3'>
                <label className="d-flex  align-items-center">Name:</label>
                <input type='text' name="name" value={user.name} onChange={handleChange}></input>
              </div>

              <div className='column m-3'>
                <label className="d-flex  align-items-center ">CurrentWeight (kg)*:</label>
                <input type="number" name="currentweight" value={user.currentWeight} onChange={handleChange}></input>
              </div>

              <div className='column m-3'>
                <label className="d-flex align-items-center ">DesiredWeight (kg)*:</label>
                <input type="number" name="desiredWeight" value={user.desiredWEight} onChange={handleChange}></input>
              </div>

              <div className='column m-3'>
                <label className="d-flex  align-items-center ">Height in(cm)*:</label>
                <input type="number" name="height" value={user.height} onChange={handleChange}></input>
              </div>

            </form>
          </div>
          <div className='w-30 h-30 d-flex' >

