import React, { useState, useEffect, } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
function Listout() {
    const [users, setUsers] = useState([])
    const [edituser, setEditUser] = useState([])
    const navigate = useNavigate()
    useEffect(() => {
        GetApi()

    }, []);

    const GetApi = () => {
        axios.get('http://localhost:1234/getUser')
            .then((res) => {
                // console.log(res,45674567876545654);
                setUsers(res.data.data)
            })
    }

    const handleDelete = (userid) => {
        console.log(userid, "=====================================")
        axios.delete('http://localhost:1234/deleteUser', { params: { id: userid } })
            .then((res) => {
                console.log(res, 456765);
                setUsers(res.data.data)

            })
    };

    const handleEditProcess = (userid) => {
        axios
            .put('http://localhost:1234/updateUser', { params: { id: userid, name: users.name, age: users.age, gender: users.gender, email: users.email } })
            .then((responce) => {

                setUsers(responce.data)
            })
            .catch((err) => {
                console.log("err" + err)
            })
    }






    // const updateUser = () => {
    //     axios
    //         .put('http://localhost:1234/updateUser')
    //         .then((responce) => {
    //             navigate('/edituser')
    //             console.log(responce)
    //         })
    //         .catch((err) => {
    //             console.log("err")
    //         })
    // }
    // console.log(users);
    return (
        <div Form className="container-fluid m-3 bg-lite ">
            <div className="column">
                <div className="col-12 mt-5" >
                    <table className='table'>

                        <tr className='table-active'>
                            <th > Id     </th>
                            <th > Name   </th>
                            <th > Age    </th>
                            <th > Gender </th>
                            <th > Email  </th>
                        </tr>

                        {users.length > 0 && users.map(({ _id, name, age, gender, email }) => {
                            // console.log(users);
                            return (
                                <tbody >
                                    <tr key={_id}>
                                        <td >  {_id}   </td>
                                        <td >  {name}   </td>
                                        <td >  {age}    </td>
                                        <td >  {gender} </td>
                                        <td >   {email}  </td>
                                        <tr>
                                            <td>
                                                <button className="btn btn-primary" onClick={handleEditProcess()} >  Edit </button>
                                                <button className="btn btn-danger" onClick={() => { handleDelete(_id); }}>Delete</button>

                                            </td>
                                        </tr>

                                    </tr>
                                </tbody>
                            )
                        })}


                    </table>
                </div>
            </div>
        </div>
    )

}
export default Listout;
