import React from 'react';
import './App.css';
import { useState } from 'react';
function App() {
  const [value, onChange] = useState(1);
  const marks = [{
    value: 10, label: '10',
    value: 20, label: '20',
    value: 30, label: '30',
    value: 40, label: '40',
    value: 50, label: '50',
  }]
  return (

    <div className='slider d-flex  align-items-center justify-content-center  ' >
      <div className="slider-parent " >
        <input type="range" min="1" max="100" value={value}
          defaultValue={30}
          onChange={({ target: { value: radius } }) => { onChange(radius); }}
          marks={marks}
        />
        <div className="buble">
          {value}
        </div>
      </div>
    </div>

  );
}
export default App;
