import React, { useState } from "react";
import axios from "axios";
function Listout() {
    const [user, setUser] = useState([])
    // useEffect(() => {
    //     console.log(user)
    //     axios.get('http://localhost:1234/getUser')
    //         .then((res) => {
    //             console.log(res);
    //             setUser(res.data.data)
    //         })
    //     return
    // }, []);

    const GetApi = () => {
        axios.get('http://localhost:1234/getUser')
            .then((res) => {
                console.log(res);
                setUser(res.data.data)
            })
    }
    const handleDelete = (index) => {
        axios.delete('http://localhost:1234/deleteUser')
            .then((res) => {
                console.log(res);
                setUser(res.data)
                const updatedUser = [...user];
                updatedUser.splice(index, 1);
                setUser(updatedUser);

            })
    };

    console.log(user);
    return (
        <div Form className="container-fluid m-3 bg-lite ">
            <div className="column">
                <div className="col-12 mt-5" >
                    <table className='table'>

                        <tr className='table-active'>
                            <th >Name</th>
                            <th >Age</th>
                            <th> Gender </th>
                            <th> Email </th>
                        </tr>

                        {user.length > 0 && user.map((item) => {
                            console.log(user);
                            return (
                                <tbody >
                                    <tr >
                                        <td >{item.name} </td>
                                        <td >{item.age}</td>
                                        <td >{item.gender}</td>
                                        <td >{item.email}</td>
                                        <tr>
                                            <button className="btn btn-primary" onClick={() => { handleDelete(GetApi()); }}>Delete</button>
                                            {/* {item.selectedOption &&
                                                <div className='menu-options'>
                                                    <div className='option' onClick={() => handleEditProcess()}>Edit</div>
                                                    <div className='option' onClick={() => handleDelete()}>Delete</div>
                                                </div>
                                            } */}

                                        </tr>
                                    </tr>
                                </tbody>
                            )
                        })}


                    </table>
                </div>
            </div>
        </div>
    )

}
export default Listout;
