import React, { useState, useEffect, } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
function Listout() {
    const [user, setUser] = useState([])
    const [edituser, setEditUser] = useState([])
    const navigate = useNavigate()
    useEffect(() => {
        GetApi()

    }, []);

    const GetApi = () => {
        axios.get('http://localhost:1234/getUser')
            .then((res) => {
                console.log(res);
                setUser(res.data.data)
            })
    }

    const handleDelete = () => {
        axios.delete('http://localhost:1234/deleteUser')
            .then((res) => {
                console.log(res);

            })
    };
    const handleEditProcess = (index) => {
        if (index !== false) {
            const editedItem = user[index];
            setEditUser(editedItem)

        }
        updateUser()
    }

    const updateUser = () => {
        axios
            .put('http://localhost:1234/updateUser')
            .then((responce) => {
                navigate('/edituser')
                cconsole.log(responce)
            })
            .catch((err) => {
                console.log("err")
            })
    }
    console.log(user);
    return (
        <div Form className="container-fluid m-3 bg-lite ">
            <div className="column">
                <div className="col-12 mt-5" >
                    <table className='table'>

                        <tr className='table-active'>
                            <th >Name</th>
                            <th >Age</th>
                            <th> Gender </th>
                            <th> Email </th>
                        </tr>

                        {user.length > 0 && user.map((item) => {
                            console.log(user);
                            return (
                                <tbody >
                                    <tr >
                                        <td >{item.name} </td>
                                        <td >{item.age}</td>
                                        <td >{item.gender}</td>
                                        <td >{item.email}</td>
                                        <tr>
                                            <td>
                                                <button className="btn btn-primary" onClick={handleEditProcess} >  Edit </button>
                                                <button className="btn btn-danger" onClick={() => { handleDelete(GetApi()); }}>Delete</button>

                                            </td>
                                            {/* {item.selectedOption &&
                                                <div className='menu-options'>
                                                    <div className='option' onClick={() => handleEditProcess()}>Edit</div>
                                                    <div className='option' onClick={() => handleDelete()}>Delete</div>
                                                </div>
                                            } */}

                                        </tr>
                                    </tr>
                                </tbody>
                            )
                        })}


                    </table>
                </div>
            </div>
        </div>
    )

}
export default Listout;
