import React from 'react';
import { useState } from 'react';
function App() {
  const [value, onChange] = useState(1);
  // const marks = { 10: '10', 20: '20', 30: '30', 40: '40' }
  return (
    <div className='slider d-flex  align-items-center justify-content-center m-5 p-5 ' >
      {/* <div className="slider-parent " > */}
      <input type="range" min="1" max="100" value={value}
        defaultValue={30}
        steps={10}
        onChange={({ target: { value: radius } }) => { onChange(radius); }}

      />
      <div className="buble">
        {value}
      </div>
      {/* </div> */}
    </div>
  );
}
export default App;
