import React from "react";
import { useState } from "react";
function App() {
  const [percent, setPercent] = useState(40);

  const decrease = () => {
    const value =
      Math.max(percent - 10, 0);
    setPercent(value);
  };

  const increase = () => {
    const value =
      Math.min(percent + 10, 100);
    setPercent(value);
  };

  const status = percent === 100 ? "success" : null;
  const color = percent === 100 ? "#03D613" : "#02749C";

  return (
    <div>
      <div>
        <ButtonGroup>
          <Button onClick={decrease}>
            -
          </Button>
          <Button onClick={increase}>
            +
          </Button>
        </ButtonGroup>
        <hr />
        <Progress.Line
          percent={percent}
          strokeColor={color}
          status={status}
        />
      </div>
    </div>

  );
}
export default App;
