import { React, useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import reportWebVitals from './reportWebVitals';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Listout from './Listout';
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <Router>
    <Routes>
      <Route path='/' element={<Home />} />
      <Route path='/form' element={< Form />} />
      <Route path='listout' element={<Listout />} />
    </Routes>
  </Router>
);
function Home() {
  return (
    < div >
      <h1 style={{ textAlign: "center" }}> <br></br>FORM <br></br></h1>
      <Link style={{ textAlign: "center", fontWeight: "bold", fontSize: "larger" }}>Gym Membership Form</Link>
    </div >
  )
}
function Form() {
  let user = {};
  const [arr, setArr] = useState([])

  const handleSubmit = (event) => {
    event.preventDefault()
    console.log(user);
    setArr([...arr, user])
    user = { name: "", currentWeight: "", height: "" }
  };
  console.log(arr);
  function handleChange(event) {
    const { name, value } = event.target
    console.log(name)
    user[name] = value
  }

  function handleWeightChange(e) {
    const { name } = e.target
    if (e.target.value.length > e.target.maxLength) {
      e.target.value = e.target.value.slice(0, e.target.maxLength)  //type of number ah max length  apply panrathu
      user[name] = e.target.value
    }

  }
  return (

    <div className="container-fluid m-3 bg-lite ">
      <div className="column">
        <div className="mt-5">
          <h2>GYM Membership Form</h2>
          <div className='row mt-5 '>
            <div className='column m-3'>
              <label className="d-flex  align-items-center">Name:</label>
              <input type='text' name="name" value={user.name} maxLength={7} onChange={handleChange}></input>
            </div>

            <div className='column m-3'>
              <label className="d-flex  align-items-center ">CurrentWeight (kg)*:</label>
              <input type="number" name="currentWeight" value={user.currentWeight} maxLength={2} onChange={handleWeightChange}></input>
            </div>

            <div className='column m-3'>
              <label className="d-flex  align-items-center ">Height in(cm)*:</label>
              <input type="number" name="height" value={user.height} maxLength={3} onChange={handleWeightChange}></input>
            </div>

          </div>
          <div className='w-30 h-30 d-flex' >
            <button className='btn btn-primary' type="onsubmit" onClick={() => { Navigate() }}> SUBMIT</button>
          </div>
        </div>
      </div>
    </div >
  );
}

