import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
function App() {
    let user = {};
    // const [arr, setArr] = useState([])
    const navigate = useNavigate()
    // const [form, setForm] = useState([])
    const [inputFields, setInputFields] = useState({ name: "", age: null, gender: "", email: "" });
    const [errors, setErrors] = useState({});
    const [submitting, setSubmitting] = useState(false)
    const validateValues = (inputValues) => {
        let errors = {};
        if (inputValues.name.length < 3) {
            errors.name = "Name is too short";
        }
        if (inputValues === null) {
            errors.age = "Age is not valid ";
        }
        if (inputValues.gender === null) {
            errors.gender = "enter the gender";
        }
        if (inputValues.email.length < 15) {
            errors.email = "Email is too short";
        }


        return errors;
    };
    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(validateValues(inputFields));
        setSubmitting(true);
        function handleApi() {
            axios.post('http://localhost:1234/registerUser', {
                name: "hjbdbjh",
                age: "12",
                email: "msnxnjh",
                gender: "male"
            })
                .then((response) => {
                    navigate('/listout')
                    console.log(response);

                })
                .catch((error) => {
                    console.log(error);
                })
        }
        handleApi()
    };
    const finishSubmit = () => {
        console.log(inputFields);
    };
    useEffect(() => {
        if (Object.keys(errors).length === 0 && submitting) {
            finishSubmit();
        }
    }, [errors, submitting]);

