        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const validateForm = () => {
        let isValid = true;
        const newErrors = {};
        if (!formData.name) {
            newErrors.name = "Name is required";
            isValid = false;
        }
        if (!formData.age) {
            newErrors.age = "Age is required";
            isValid = false;
        }
        if (!formData.gender) {
            newErrors.gender = "Gender is required";
            isValid = false;
        }
        if (!formData.email) {
            newErrors.email = "Email is required";
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };

    // const updateUser = (userid) => {
    //     axios
    //         .put('http://localhost:1234/updateUser', { params: { id: userid, name: formData.name, age: formData.age, gender: formData.gender, email: formData.email } })
    //         .then((responce) => {

    //             setFormData(responce.data)
    //         })
    //         .catch((err) => {
    //             console.log("err" + err)
